import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const ProfilePage = () => {
    const navigate = useNavigate();
    const [username, setUsername] = useState('Guest');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUserData = async () => {
            const user = supabase.auth.getUser(); // Gets the current user details

            if (!user) {
                navigate('/'); // If no user is found, redirect to login
                return;
            }

            const { data, error } = await supabase
                .from('profiles')
                .select('username')
                .eq('user_id', user.id)
                .single();

            if (error) {
                console.error('Error fetching user data:', error.message);
                navigate('/login'); // Handle errors possibly by redirecting to login or showing a message
            } else if (data) {
                setUsername(data.username);
            }

            setLoading(false);
        };

        fetchUserData();
    }, [navigate]);

    const handleLogout = async () => {
        await supabase.auth.signOut();
        navigate('/login'); // Redirects user to login page after logout
    };

    if (loading) {
        return <div>Loading...</div>; // Optionally handle loading state better
    }

    return (
        <div className="w-full max-w-md p-8 space-y-6 bg-white rounded-lg shadow-lg">
            <h1 className="text-2xl text-center">Welcome back, {username}</h1>
            <button onClick={handleLogout} className="w-full bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded transition-colors duration-300">
                Logout
            </button>
        </div>
    );
};

export default ProfilePage;
