// MovieCard.js
import React, { useState } from 'react';

const MovieCard = ({ id, title, medium }) => {
    const [open, setOpen] = useState(false);

    const handleIcon = (medium) => {
        switch (medium) {
            case 'Book':
                return "ðŸ“–"; // Book Icon
            case 'Movie':
                return "ðŸŽ¬"; // Movie Icon
            case 'YouTube':
                return "ðŸŽ¥"; // YouTube Icon
            case 'TV':
                return "ðŸ“º"; // TV Icon
            default:
                return null;
        }
    };

    return (
        <div className="wrapper mx-auto border-2 rounded-lg w-full mb-2">
            <div className="flex justify-between items-center p-4">
                <span className="text-lg font-semibold">{title}</span>
                <div>
                    <span className="pr-4">{handleIcon(medium)}</span>
                    <button onClick={() => setOpen(!open)} className="p-2">
                        {open ? 
                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" className="w-6 h-6">
                                <path stroke-linecap="round" stroke-linejoin="round" d="m4.5 15.75 7.5-7.5 7.5 7.5" />
                            </svg>
                        : 
                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" className="w-6 h-6">
                                <path stroke-linecap="round" stroke-linejoin="round" d="m19.5 8.25-7.5 7.5-7.5-7.5" />
                            </svg>
                        }
                    </button>
                </div>
            </div>
            {open && (
                <div className="p-4 text-sm font-light">
                    {/* Additional details can be placed here */}
                </div>
            )}
        </div>
    );
};

export default MovieCard;
