// HomePage.js
import React, { useState, useEffect, useRef } from 'react';
import { SignedIn, SignedOut, SignIn, SignUp, useUser } from '@clerk/clerk-react';
import { supabase } from '../supabaseClient';
import MovieCard from '../components/common/MovieCard';

function HomePage({ showLogin, showSignup, toggleLogin, toggleSignup }) {
  const loginFormRef = useRef(null);
  const signupFormRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      // Check if the click is outside the login form
      if (showLogin && loginFormRef.current && !loginFormRef.current.contains(event.target)) {
        toggleLogin();  // Toggle login visibility if outside click
      }
      // Check if the click is outside the signup form
      if (showSignup && signupFormRef.current && !signupFormRef.current.contains(event.target)) {
        toggleSignup();  // Toggle signup visibility if outside click
      }
    };

    // Attach the listener to the document
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      // Clean up the listener on component unmount
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showLogin, showSignup, toggleLogin, toggleSignup]);  // Dependencies for the effect

  return (
    <div className={`bg-gray-900 text-white min-h-screen relative flex items-center justify-center`}>
      <div className="container mx-auto mt-10 px-4">
        <div className={`relative ${showLogin || showSignup ? 'blur-bg' : ''}`}>
          <SignedOut>
            {showLogin && (
              <div ref={loginFormRef} className="form-container">
                <SignIn />
              </div>
            )}
            {showSignup && (
              <div ref={signupFormRef} className="form-container">
                <SignUp path="/register" signInUrl="/login" />
              </div>
            )}
          </SignedOut>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
