import React from 'react';

const JobCard = ({ jobTitle, companyName, location, jobType, tags }) => {
  return (
    <div className="bg-gray-50 p-4 sm:p-6 rounded-md overflow-hidden">
      <div className="flex flex-col sm:grid sm:grid-cols-3 sm:gap-4">
        <div className="flex justify-center sm:justify-start">
          {/* Image placeholder */}
          <img
            className="h-8 sm:h-10 md:h-11 w-8 sm:w-10 md:w-11 bg-white border border-gray-100 rounded-full"
            src="/path-to-your-image.jpg"
            alt={`${companyName} logo`}
          />
        </div>

        {/* Job details */}
        <div className="sm:col-span-2">
          <h2 className="text-lg sm:text-xl md:text-2xl text-gray-800 font-bold mt-2 sm:mt-0">
            {jobTitle}
          </h2>
          <h3 className="text-base md:text-lg text-gray-800">
            {companyName}
          </h3>
          <div className="flex flex-row flex-wrap mt-3 space-x-1 sm:space-x-2 text-sm">
            <h4 className="inline">{location}</h4>
            <span aria-hidden="true">Â·</span>
            <h4 className="inline">{jobType}</h4>
          </div>
          <ul className="flex flex-wrap mt-1 md:mt-2 -mx-1">
            {tags.map((tag) => (
              <li key={tag} className="border border-gray-300 mx-1 my-1 rounded-md group hover:border-purple transition-colors">
                <a className="block group-hover:text-purple p-1 text-xs">
                  {tag}
                </a>
              </li>
            ))}
          </ul>
        </div>

        {/* Status and feature tag */}
        <div className="flex justify-between sm:justify-end items-center sm:col-span-3 mt-4 sm:mt-0">
          <span className="text-xs font-semibold">Today</span>
          <div className="text-white font-medium bg-gradient-to-b from-gray-600 to-gray-700 px-4 py-1.5 rounded-full text-xs">
            <span>Featured</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JobCard;
