import React, { useState } from 'react';
import Header from '../components/common/header';
import MovieCard from '../components/common/MovieCard'; // This needs to be created or adjusted
import SearchBar from '../components/common/SearchBar'; // Ensure this component is suitable for movies

function HomePage() {
  // State to hold movie data
  const [movies, setMovies] = useState([
    {
      id: 1,
      title: 'Inception',
      year: 2010,
      genres: ['Action', 'Sci-Fi', 'Thriller']
    },
  ]);

  // Handler for search functionality (simplified)
  const handleSearch = (event) => {
    if (event.key === 'Enter') {
      console.log('Search for movies with:', event.target.value);
    }
  };

  // Function to add a new movie
  const addMovie = (movie) => {
    setMovies([...movies, { ...movie, id: movies.length + 1 }]);
  };

  // Function to remove a movie
  const removeMovie = (id) => {
    setMovies(movies.filter(movie => movie.id !== id));
  };

  // Function to update a movie
  const updateMovie = (updatedMovie) => {
    setMovies(movies.map(movie => movie.id === updatedMovie.id ? updatedMovie : movie));
  };

  return (
    <div className="min-h-screen flex flex-col bg-white">
      <Header />

      {/* Search area */}
      <div className="bg-gray-200 p-4">
        <div className="container mx-auto">
          <SearchBar placeholder="Search movies..." onSearch={handleSearch} />
        </div>
      </div>

      {/* Movie list header */}
      <div className="container mx-auto my-4">
        <h2 className="text-2xl font-semibold">Manage Your Movie Lists</h2>
      </div>

      {/* Movie Listings */}
      <div className="container mx-auto p-4">
        {movies.map(movie => (
          <MovieCard key={movie.id} movie={movie} removeMovie={removeMovie} updateMovie={updateMovie} />
        ))}
      </div>

      <footer className="bg-gray-200 text-center p-4">
        Â© 2024 Movie Collector's Hub, Inc.
      </footer>
    </div>
  );
}

export default HomePage;
