// HomePage.js
import React, { useState, useEffect, useRef } from 'react';
import { SignedIn, SignedOut, SignIn, SignUp, useUser } from '@clerk/clerk-react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';
import MovieCard from '../components/common/MovieCard';

function HomePage({ showLogin, showSignup, toggleLogin, toggleSignup }) {
  const [media, setMovies] = useState([]);
  const loginFormRef = useRef(null);
  const signupFormRef = useRef(null);
  const { user } = useUser();
  const navigate = useNavigate();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (loginFormRef.current && !loginFormRef.current.contains(event.target)) {
        if (showLogin) toggleLogin();
      }
      if (signupFormRef.current && !signupFormRef.current.contains(event.target)) {
        if (showSignup) toggleSignup();
      }
    };

    if (showLogin || showSignup) {
      document.addEventListener('click', handleClickOutside);
    }

    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [showLogin, showSignup]);

  useEffect(() => {
    const redirectToProfile = async () => {
      if (user) {
        const { id } = user;
        const { data, error } = await supabase
          .from('profiles')
          .select('username')
          .eq('user_id', id)
          .single();

        if (error) {
          console.error("Database fetch error:", error.message);
        } else {
          navigate(`/${data.username}`);
        }
      }
    };

    redirectToProfile();
  }, [user, navigate]);

  const handleSignUp = async (user) => {
    const { id, emailAddresses } = user;
    const email = emailAddresses[0].emailAddress;

    const { error: insertError } = await supabase
      .from('profiles')
      .insert([
        { user_id: id, email: email, username: email.split('@')[0] } // Assign a default username based on email
      ]);

    if (insertError) {
      console.error("Database insertion error:", insertError.message);
    } else {
      console.log('Signup and profile creation successful:', user);
      navigate(`/${email.split('@')[0]}`);
    }
  };

  const handleSignIn = async (user) => {
    const { id } = user;

    const { data, error } = await supabase
      .from('profiles')
      .select('username')
      .eq('user_id', id)
      .single();

    if (error) {
      console.error("Database fetch error:", error.message);
    } else {
      console.log('Login successful:', data);
      navigate(`/${data.username}`);
    }
  };

  return (
    <div className="container mx-auto mt-10 px-4">
      <div className="relative">
        <SignedIn>
          <div>Welcome, {user?.firstName}!</div>
          {/* Your application content goes here */}
        </SignedIn>
        <SignedOut>
          {showLogin && (
            <div
              ref={loginFormRef}
              className="form-container"
              onMouseDown={(e) => e.stopPropagation()} // Prevents form from closing when clicking inside
            >
              <SignIn afterSignIn={handleSignIn} />
            </div>
          )}
          {showSignup && (
            <div
              ref={signupFormRef}
              className="form-container"
              onMouseDown={(e) => e.stopPropagation()} // Prevents form from closing when clicking inside
            >
              <SignUp afterSignUp={handleSignUp} />
            </div>
          )}
        </SignedOut>
      </div>
    </div>
  );
}

export default HomePage;
