import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const SignupPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const { user, error } = await supabase.auth.signUp({
            email,
            password,
        });

        if (error) {
            setError(error.message);
        } else {
            console.log('Signup successful:', user);
            // Redirect or perform further actions
        }
    };

    const handleGoogleSignIn = async () => {
        const { user, error } = await supabase.auth.signIn({
            provider: 'google',
        });

        if (error) {
            setError(error.message);
        } else {
            console.log('Google sign in successful:', user);
            // Redirect or perform further actions
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="w-full max-w-md p-8 space-y-6 bg-white rounded-lg shadow-lg">
                <h1 className="text-2xl font-bold text-center">Create an Account</h1>
                {error && <p className="text-red-500 text-center">{error}</p>}
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Email"
                            className="w-full p-2 border border-gray-300 rounded"
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Password"
                            className="w-full p-2 border border-gray-300 rounded"
                            required
                        />
                    </div>
                    <button type="submit" className="w-full bg-stone-500 hover:bg-neutral-400 text-white font-bold py-2 px-4 rounded transition-colors duration-300">
                        Sign Up
                    </button>
                </form>
                <p className="text-gray-300 text-center mt-4">Or</p>
                <button onClick={handleGoogleSignIn} className="w-full bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mt-2">
                    Sign Up with Google
                </button>
                <p className="text-center text-gray-300 mt-4">
                    Already have an account? <Link to="/login" className="text-blue-500 hover:underline">Login</Link>
                </p>
            </div>
        </div>
    );
};

export default SignupPage;
