import React, { useEffect } from 'react';
import { SignedIn, SignedOut, SignIn, SignUp } from '@clerk/clerk-react';

function HomePage({ showLogin, showSignup, toggleLogin, toggleSignup }) {
  useEffect(() => {
    const handleSignInLinkClick = (event) => {
      if (event.target.classList.contains('cl-sign-in')) {
        event.preventDefault();
        toggleLogin();
        toggleSignup();
      }
    };

    document.addEventListener('click', handleSignInLinkClick);

    return () => {
      document.removeEventListener('click', handleSignInLinkClick);
    };
  }, [toggleLogin, toggleSignup]);

  return (
    <div className={`bg-[#232323] text-white min-h-screen relative flex items-center justify-center`}>
      <div className="container mx-auto mt-10 px-4">
        <div className={`relative ${showLogin || showSignup ? 'blur-bg' : ''}`}>
          {(showLogin || showSignup) && (
            <div className="backdrop" onClick={() => {
              if (showLogin) toggleLogin();
              if (showSignup) toggleSignup();
            }}></div>
          )}

          <SignedOut>
            {showLogin && (
              <div className="form-container">
                <SignIn afterSignInUrl="/" />
              </div>
            )}
            {showSignup && (
              <div className="form-container">
                <SignUp path="/sign-up" signInUrl="/" afterSignUpUrl="/" />
              </div>
            )}
          </SignedOut>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
