import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const WatchlistPage = () => {
    const [watchlistName, setWatchlistName] = useState('');
    const [username, setUsername] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch user data and username from the profiles table
        const fetchUserData = async () => {
            const user = supabase.auth.getUser(); // Gets the current session user details
            if (!user) {
                navigate('/pickles'); // Redirect if not logged in
                return;
            }

            const { data: profile, error } = await supabase
                .from('profiles')
                .select('username')
                .eq('user_id', user.id)
                .single();

            if (error) {
                console.error('Error fetching user data:', error.message);
                navigate('/'); // Redirect on error
            } else if (profile) {
                setUsername(profile.username); // Set the username
            }
        };

        fetchUserData();
    }, [navigate]);

    const createWatchlist = async () => {
        if (!watchlistName) return;

        const { data: { user } } = await supabase.auth.getUser();
        const { data, error } = await supabase
            .from('watchlists')
            .insert([{ name: watchlistName, user_id: user.id }]);

        if (error) {
            console.error('Error creating watchlist:', error.message);
            return;
        }

        // Use the username fetched from the profiles table
        navigate(`/${username}/${watchlistName}`);
        setWatchlistName(''); // Clear the field after creation
    };

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-xl font-bold">Create a New Watchlist</h1>
            <input
                type="text"
                value={watchlistName}
                onChange={(e) => setWatchlistName(e.target.value)}
                placeholder="Enter Watchlist Name"
                className="border p-2"
            />
            <button onClick={createWatchlist} className="ml-2 bg-blue-500 text-white p-2 rounded">
                Create Watchlist
            </button>
        </div>
    );
};

export default WatchlistPage;
