import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import ProfileHeader from '../components/common/ProfileHeader';

const ProfilePage = () => {
    const navigate = useNavigate();
    const [username, setUsername] = useState('Guest');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUserData = async () => {
            const { data: { user } } = await supabase.auth.getUser(); // Gets the current user details

            if (!user) {
                navigate('/'); // If no user is found, redirect to login
                return;
            }

            const { data: profile, error } = await supabase
                .from('profiles')
                .select('username')
                .eq('user_id', user.id)
                .single();

            if (error) {
                console.error('Error fetching user data:', error.message);
                navigate('/');
            } else if (profile) {
                setUsername(profile.username);
            }

            setLoading(false);
        };

        fetchUserData();
    }, [navigate]);

    const handleLogout = async () => {
        await supabase.auth.signOut();
        navigate('/');
    };

    const createWatchlist = async () => {
        const { data, error } = await supabase
            .from('watchlists')
            .insert([
                { name: 'New Watchlist', user_id: supabase.auth.user().id }
            ]);

        if (error) {
            console.error('Error creating watchlist:', error.message);
            return;
        }

        navigate(`/${username}/${data[0].name}`);
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="w-full h-screen flex flex-col items-center justify-start pt-20">
            <h1 className="text-2xl text-center">
                Welcome back, <span className="underline">{username}</span>
            </h1>
            <button onClick={createWatchlist} className="mt-5 p-2 bg-blue-500 text-white rounded">
                New Watchlist
            </button>
        </div>
    );
};

export default ProfilePage;
