// HomePage.js
import React, { useState, useEffect, useRef } from 'react';
import { SignedIn, SignedOut, SignIn, SignUp, useUser } from '@clerk/clerk-react';
import { supabase } from '../supabaseClient';
import MovieCard from '../components/common/MovieCard';

function HomePage({ showLogin, showSignup, toggleLogin, toggleSignup }) {
  const [media, setMovies] = useState([]);
  const loginFormRef = useRef(null);
  const signupFormRef = useRef(null);
  const { user } = useUser();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (loginFormRef.current && !loginFormRef.current.contains(event.target) && showLogin) {
        toggleLogin();
      }
      if (signupFormRef.current && !signupFormRef.current.contains(event.target) && showSignup) {
        toggleSignup();
      }
    };

    // Attach the listener to the document
    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      // Clean up the listener
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showLogin, showSignup, toggleLogin, toggleSignup]);

  useEffect(() => {
    if (user) {
      // Fetch user-specific data from Supabase if needed
    }
  }, [user]);

  return (
    <div className={`bg-gray-900 text-white min-h-screen relative flex items-center justify-center`}>
      <div className="container mx-auto mt-10 px-4">
        <div className={`relative ${showLogin || showSignup ? 'blur-bg' : ''}`}>
          <SignedIn>
            <div>Welcome, {user?.firstName}!</div>
            {/* Your application content goes here */}
          </SignedIn>
          <SignedOut>
  {showLogin && (
    <div ref={loginFormRef} className="form-container">
      <div className="form-inner">
        <SignIn
        appearance={{
          variables: {
            colorPrimary: "red",
            colorText: "white"
          }
        }}

      />
      </div>
    </div>
  )}
  {showSignup && (
    <div ref={signupFormRef} className="form-container">
      <div className="form-inner">
        <SignUp path="/register" signInUrl="/login" />
      </div>
    </div>
  )}
</SignedOut>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
