import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const LoginPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLoginError = (error) => {
        if (error.message.includes("not found")) {
          setError("We can't seem to find your account. Try again, or don't I guess.");
        } else {
          setError("Something went wrong. Please try again later.");
        }
    }


    const handleSubmit = async (e) => {
        e.preventDefault();

        const { data, error: dbError } = await supabase
            .from('profiles')  // Changed from 'users' to 'profiles'
            .select('*')
            .eq('username', username)
            .single();

        if (dbError) {
            setError(dbError);
            return;
        }

        if (data) {
            const { user, error: loginError } = await supabase.auth.signInWithPassword({
                email: data.email,  // Assuming email is stored in the data
                password: password,
            });

            if (loginError) {
                setError("loginError.message");
            } else {
                console.log('Login successful:', user);
                navigate('/profile'); // Redirect to the profile page
            }
        } else {
            setError("User not found");
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="w-full max-w-md p-8 space-y-6 bg-white rounded-lg shadow-lg">
                <h1 className="text-2xl font-bold text-center">Login to Your Account</h1>
                {error && <p className="text-red-500 text-center">{error}</p>}
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <input
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            placeholder="Username"
                            className="w-full p-2 border border-gray-300 rounded"
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Password"
                            className="w-full p-2 border border-gray-300 rounded"
                            required
                        />
                    </div>
                    <button type="submit" className="w-full bg-stone-400 hover:bg-neutral-300 text-white font-bold py-2 px-4 rounded transition-colors duration-300">
                        Login
                    </button>
                </form>
                <p className="text-center">
                    No account? <Link to="/signup" className="text-blue-500 font-bold hover:transition-all">Create one</Link>
                </p>
            </div>
        </div>
    );
};

export default LoginPage;
