import React from 'react';

const JobCard = ({ jobTitle, companyName, location, jobType, tags }) => {
  return (
    <div className="grid text-sm text-gray-600 bg-gray-50 p-4 sm:p-6" style={{ gridTemplateColumns: 'auto minmax(0, 1fr)' }}>
      {/* Image placeholder */}
      <img
        className="h-8 sm:h-10 md:h-11 w-8 sm:w-10 md:w-11 bg-white border border-gray-100 sm:mr-4 rounded-full"
        src="/path-to-your-image.jpg"
        alt={`${companyName} logo`}
      />

      {/* Job details */}
      <div className="col-start-1 sm:col-start-2 row-start-2 sm:row-start-1">
        <h2 className="text-lg sm:text-xl md:text-2xl text-gray-800 font-bold mt-2 sm:mt-0">
          {jobTitle}
        </h2>
        <h3 className="text-base md:text-lg text-gray-800">
          {companyName}
        </h3>
      </div>

      {/* Job meta and tags */}
      <div className="col-start-1 sm:col-start-2 col-end-2 sm:col-end-4">
        <div className="flex flex-row flex-wrap mt-3 space-x-1 sm:space-x-2">
          <h4 className="inline">{location}</h4>
          <span aria-hidden="true">Â·</span>
          <h4 className="inline">{jobType}</h4>
        </div>
        <ul className="flex flex-wrap -mb-1 sm:-mb-0.5 mt-1 md:mt-2 -mx-1 sm:-mx-0.5">
          {tags.map((tag) => (
            <li key={tag} className="border border-gray-300 m-1 sm:m-0.5 rounded-md group hover:border-purple transition-colors">
              <a className="block group-hover:text-purple p-1">
                {tag}
              </a>
            </li>
          ))}
        </ul>
      </div>

      {/* Status and feature tag */}
      <div className="col-start-3 row-start-1 flex items-baseline justify-end self-center sm:self-auto">
        <div className="inline-block leading-loose sm:mr-4">
          <span>Today</span>
        </div>
        <div className="hidden sm:inline-block text-white font-medium bg-gradient-to-b from-gray-600 to-gray-700 px-4 py-1.5 rounded-full">
          <span>Featured</span>
        </div>
      </div>
    </div>
  );
};

export default JobCard;
