// App.js
import './index.css';
import './App.css';
import React, { lazy, Suspense } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Layout from './components/common/Layout';  // Ensure this path is correct
import "./fonts/graphik/GraphikLight.otf";

const Login = lazy(() => import('./pages/LoginPage'));
const Signup = lazy(() => import('./pages/SignupPage'));
const ForgotPassword = lazy(() => import('./pages/ForgotPasswordPage')); // New import
const HomePage = lazy(() => import('./pages/HomePage'));  // Optionally lazy-load HomePage too

function App() {
  return (
    <Router>
      <div className="App">
        <Suspense fallback={<div>Loading...</div>}>
          <Routes>
            <Route path="/" element={<Layout />}>
              <Route path="/login" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
              <Route path="/forgot-password" element={<ForgotPassword />} />  // New route
              <Route index element={<HomePage />} />
            </Route>
          </Routes>
        </Suspense>
      </div>
    </Router>
  );
}

export default App;
