import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Draggable } from 'react-beautiful-dnd';
import { supabase } from '../../supabaseClient'; // Adjust the path as needed
import debounce from 'lodash/debounce'; // Import debounce

const MovieCard = ({ id, title, medium, length, date, synopsis, image, url, creator, addedBy, status, notes, rating, onDelete, onNotesChange, onStatusChange, onRatingChange, index, isOpen, setIsOpen }) => {
    const [localNotes, setLocalNotes] = useState(notes);
    const [localRating, setLocalRating] = useState(rating);
    const [localStatus, setLocalStatus] = useState(status);
    const notesRef = useRef(notes);

    useEffect(() => {
        setLocalNotes(notes);
    }, [notes]);

    useEffect(() => {
        setLocalRating(rating);
    }, [rating]);

    const handleNotesChange = (e) => {
        setLocalNotes(e.target.value);
        notesRef.current = e.target.value;
        debouncedUpdateNotes(id, e.target.value);
    };

    const handleRatingChange = (e) => {
        const newRating = e.target.value;
        setLocalRating(newRating);
        debouncedUpdateRating(id, newRating);
    };

    const debouncedUpdateNotes = useCallback(debounce(async (id, notes) => {
        await onNotesChange(id, notes);
    }, 300), []);

    const debouncedUpdateRating = useCallback(debounce(async (id, rating) => {
        await onRatingChange(id, rating);
    }, 300), []);

    const handleNotesBlur = async () => {
        if (notesRef.current !== notes) {
            await onNotesChange(id, notesRef.current);
        }
    };

    const handleRatingBlur = async () => {
        if (localRating !== rating) {
            await onRatingChange(id, localRating);
        }
    };

    const handleStatusChange = async (newStatus) => {
        setLocalStatus(newStatus);
        await onStatusChange(id, newStatus);
    };

    const handleToggle = () => {
        setIsOpen(id, !isOpen);
    };

    const formatDate = (date) => {
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Intl.DateTimeFormat('en-US', options).format(new Date(date));
    };

    return (
        <Draggable draggableId={id.toString()} index={index}>
            {(provided) => (
                <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    className="movie-card p-2 overflow-hidden flex flex-col"
                >
                    <div className="movie-card-header w-auto justify-between flex items-center">
                        <div className="flex items-center">
                            <div className="medium-icon pl-3 cursor-move" {...provided.dragHandleProps}>{handleIcon(medium)}</div>
                            <div className="item-title font-bold px-3 text-xl text-left flex items-center">
                                <div>
                                    <div>{title}</div>
                                    <div className="text-sm text-gray-400">
                                        {creator} &middot; {formatDate(date)}
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="flex items-center">
                            <select
                                className={`status-indicator ${localStatus ? `status-${localStatus.toLowerCase().replace(' ', '-')}` : ''}`}
                                value={localStatus}
                                onChange={(e) => handleStatusChange(e.target.value)}
                            >
                                <option value="to consume">to consume</option>
                                <option value="consuming">consuming</option>
                                <option value="consumed">consumed</option>
                            </select>
                            <button onClick={() => onDelete(id)} className="remove-button py-2 px-4">Delete</button>
                            <button onClick={handleToggle} className="p-2">
                                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className={`w-6 h-6 arrow ${isOpen ? 'arrow-down' : 'arrow-up'}`}>
                                    <path strokeLinecap="round" strokeLinejoin="round" d="m4.5 15.75 7.5-7.5 7.5 7.5" />
                                </svg>
                            </button>
                        </div>
                    </div>
                    {isOpen && (
                        <div className="p-4 flex">
                            {url && url.includes('youtube') && (
                                <div className="w-1/3">
                                    <iframe
                                        width="100%"
                                        height="200"
                                        src={`https://www.youtube.com/embed/${url.split('v=')[1]}`}
                                        title={title}
                                        frameBorder="0"
                                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                        allowFullScreen
                                    ></iframe>
                                </div>
                            )}
                            <div className="w-2/3 pl-4">
                                <div className="text-sm text-gray-400 text-right">
                                    Added by: {addedBy} on {formatDate(new Date())}
                                </div>
                                <div className="text-sm text-gray-400 flex items-center justify-end">
                                    Rating:
                                    <input
                                        type="text"
                                        className="ml-2 p-1 bg-gray-200 text-gray-800 rounded w-12"
                                        value={localRating}
                                        onChange={handleRatingChange}
                                        onKeyDown={(e) => {
                                            if (e.key === 'Enter') {
                                                e.target.blur();
                                            }
                                        }}
                                        onBlur={handleRatingBlur}
                                        placeholder="1-10"
                                    />
                                </div>
                                <div className="text-sm text-gray-400 mt-4">Notes:</div>
                                <textarea
                                    className="notes w-full mt-2 p-2 bg-gray-200 text-gray-800 rounded"
                                    rows="4"
                                    value={localNotes}
                                    onChange={handleNotesChange}
                                    onBlur={handleNotesBlur}
                                    onKeyDown={(e) => {
                                        if (e.key === 'Enter') {
                                            e.target.blur();
                                        }
                                    }}
                                    placeholder="Add your notes here..."
                                />
                            </div>
                        </div>
                    )}
                </div>
            )}
        </Draggable>
    );
};

export default MovieCard;
