import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate
import { supabase } from '../supabaseClient';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Initialize the useNavigate hook

    const handleSubmit = async (e) => {
        e.preventDefault();
        const { user, error } = await supabase.auth.signInWithPassword({
            email: email,
            password: password,
        });

        if (error) {
            setError(error.message);
        } else {
            console.log('Login successful:', user);
            navigate('/profile'); // Redirect to the profile page
        }
    };

    return (
        <div className="min-h-screen flex flex-col bg-black">
            <div className="flex-grow container mx-auto p-4 text-center">
                <h1 className="text-white font-extrabold text-4xl md:text-6xl lg:text-7xl tracking-tight mt-3">Login to Your Account</h1>
                {error && <p className="text-red-500">{error}</p>}
                <div className="mt-10">
                    <form onSubmit={handleSubmit} className="max-w-md mx-auto">
                        <div className="mb-4">
                            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" className="w-full p-2 border border-gray-300 rounded" required />
                        </div>
                        <div className="mb-6">
                            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" className="w-full p-2 border border-gray-300 rounded" required />
                        </div>
                        <button type="submit" className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Login</button>
                    </form>
                    <p className="text-white mt-4">Don't have an account? <Link to="/signup" className="text-blue-500 hover:underline">Sign Up</Link></p>
                </div>
            </div>
            <footer className="bg-gray-200 text-center p-4">
                Kittler, designed by Emily & Midhun, for you.
            </footer>
        </div>
    );
};

export default LoginPage;
