import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';

// Initialize Supabase client
const supabaseUrl = 'https://klligruouvwvqrwrxqcj.supabase.co'; // Replace with your Supabase project URL
const supabaseAnonKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImtsbGlncnVvdXZ3dnFyd3J4cWNqIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MDc1MDg5NTQsImV4cCI6MjAyMzA4NDk1NH0.9DI6I1eD9mr6wzlvePSMTdr6DQctU4Mh9vGv8XD8WJ4'; // Replace with your Supabase anon key
const supabase = createClient(supabaseUrl, supabaseAnonKey);

function LoginPage() {
  const [credentials, setCredentials] = useState({
    username: '',
    password: '',
  });
  const [error, setError] = useState(''); // State to handle any login error

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({
      ...credentials,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { username, password } = credentials;

    // Using Supabase for authentication
    const { error } = await supabase.auth.signIn({
      email: username,
      password: password,
    });

    if (error) {
      setError(error.message);
    } else {
      // Login successful
      console.log('Login successful!');
      // Redirect or perform further actions
      setError(''); // Clear any errors
      // Reset form fields
      setCredentials({ username: '', password: '' });
    }
  };

  return (
    <div className="login-page">
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Display any login error */}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username or Email</label>
          <input
            type="text"
            id="username"
            name="username"
            value={credentials.username}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={credentials.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
