import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';

function HomePage({ showLogin, showSignup, toggleLogin, toggleSignup }) {
  const loginFormRef = useRef(null);
  const signupFormRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (loginFormRef.current && !loginFormRef.current.contains(event.target) && showLogin) {
        toggleLogin();
      }
      if (signupFormRef.current && !signupFormRef.current.contains(event.target) && showSignup) {
        toggleSignup();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showLogin, showSignup, toggleLogin, toggleSignup]);

  return (
    <div className="container mx-auto mt-10 px-4">
      {showLogin && (
        <div ref={loginFormRef} className="absolute inset-0 flex justify-center items-center">
          <LoginPage />
        </div>
      )}
      {showSignup && (
        <div ref={signupFormRef} className="absolute inset-0 flex justify-center items-center">
          <SignupPage />
        </div>
      )}
      <div className="flex justify-between items-center mb-4">
        <button onClick={toggleSignup} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          Sign Up
        </button>
        <button onClick={toggleLogin} className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded">
          Login
        </button>
      </div>
    </div>
  );
}

export default HomePage;
