import React from 'react';

const MovieCard = ({ title, description, imageUrl, medium, rating, genre }) => {
  return (
    <div className="max-w-sm rounded overflow-hidden shadow-lg bg-white my-4">
      <img className="w-full" src={imageUrl} alt={`Poster of ${title}`} />
      <div className="px-6 py-4">
        <div className="font-bold text-xl mb-2">{title}</div>
        <p className="text-gray-700 text-base">
          {description}
        </p>
      </div>
      <div className="px-6 pt-4 pb-2">
        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">{medium}</span>
        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">{rating}</span>
        <span className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mb-2">{genre}</span>
      </div>
    </div>
  );
};

export default MovieCard;
