import React, { useState } from 'react';
import { supabase } from '../supabaseClient'; // Adjust the path as needed

const MovieCard = ({ id, title, medium, length, date, synopsis, onDelete }) => {
    const [open, setOpen] = useState(false);

    const handleIcon = (medium) => {
        switch (medium) {
            case 'Book':
                return <span>📖</span>;
            case 'Movie':
                return <span>🎬</span>;
            case 'YouTube':
                return <span>🎥</span>;
            case 'TV':
                return <span>📺</span>;
            default:
                return null;
        }
    };

    const handleDelete = async () => {
        const { error } = await supabase
            .from('media_items')
            .delete()
            .match({ id: id });

        if (error) {
            console.error('Error deleting media item:', error.message);
        } else {
            onDelete(id); // Callback function to update state in the parent component
        }
    };

    return (
        <div className="movie-card p-2 overflow-hidden flex flex-col">
            <div className="movie-card-header w-auto justify-between flex items-center">
                <div className="justify-between flex items-center">
                    <div className="medium-icon pl-3">{handleIcon(medium)}</div>
                    <div className="item-title font-bold float-left px-10 py-3 text-xl text-left">{title}</div>
                </div>
                <div className="flex items-center float-right">
                    <button onClick={handleDelete} className="remove-button py-2 pr-5 pl-12">
                        🗑️
                    </button>
                    <button onClick={() => setOpen(!open)} className="p-2">
                        {open ? 'Hide Details' : 'Show Details'}
                    </button>
                </div>
            </div>
            {open && (
                <div className="p-4">
                    <p>Date Added: {date}</p>
                    <p>Length: {length}</p>
                    <p>Synopsis: {synopsis}</p>
                </div>
            )}
        </div>
    );
};

export default MovieCard;


