import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const MediaPage = () => {
    const [mediaItems, setMediaItems] = useState([]);
    const [newMedia, setNewMedia] = useState('');
    const [medium, setMedium] = useState(''); // State for the medium
    const { username, watchlistName } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        fetchMediaItems();
    }, []);

    const fetchMediaItems = async () => {
        const { data: watchlist, error: watchlistError } = await supabase
            .from('watchlists')
            .select('id')
            .eq('name', watchlistName)
            .single();

        if (watchlistError) {
            console.error('Error fetching watchlist ID:', watchlistError.message);
            return;
        }

        const { data: media, error: mediaError } = await supabase
            .from('media_items')
            .select('*')
            .eq('watchlist_id', watchlist.id);

        if (mediaError) {
            console.error('Error fetching media items:', mediaError.message);
        } else {
            setMediaItems(media);
        }
    };

    const addMediaItem = async () => {
        if (!newMedia || !medium) return;  // Check if media name and medium are selected

        const { data: watchlist, error: watchlistError } = await supabase
            .from('watchlists')
            .select('id')
            .eq('name', watchlistName)
            .single();

        if (watchlistError) {
            console.error('Error fetching watchlist ID:', watchlistError.message);
            return;
        }

        const { data, error } = await supabase
            .from('media_items')
            .insert([{ title: newMedia, watchlist_id: watchlist.id, medium: medium }]);

        if (error) {
            console.error('Error adding media item:', error.message);
        } else {
            setMediaItems([...mediaItems, ...data]);  // Update the local state to display new media
            setNewMedia('');
            setMedium('');  // Reset medium selection
        }
    };

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-xl font-bold">{`Media in "${watchlistName}"`}</h1>
            {mediaItems.map((item) => (
                <div key={item.id} className="p-2 border-b">
                    {item.title} - {item.medium}
                </div>
            ))}
            <input
                type="text"
                value={newMedia}
                onChange={(e) => setNewMedia(e.target.value)}
                placeholder="Add New Media"
                className="border p-2 mt-4"
            />
            <select
                value={medium}
                onChange={(e) => setMedium(e.target.value)}
                className="border p-2 mt-4"
            >
                <option value="">Select Medium</option>
                <option value="Movie">Movie</option>
                <option value="Book">Book</option>
                <option value="YouTube">YouTube</option>
                <option value="TV">TV Show</option>
            </select>
            <button onClick={addMediaItem} className="ml-2 bg-blue-500 text-white p-2 rounded">
                Add Media
            </button>
        </div>
    );
};

export default MediaPage;
