// Header.js
import React from 'react';
import { Link } from 'react-router-dom';

function Header({ showLogin, showSignup, toggleLogin, toggleSignup }) {
    return (
        <header className="bg-zinc-700 text-white p-4 shadow-md">
            <div className="container mx-auto flex justify-between items-center">
                <div className="flex items-center">
                    <div className="my-1 mr-6">
                        <h1 className="font-bold text-2xl text-left">Kittler</h1>
                        <h2 className="italic text-left">switch off all apparatuses.</h2>
                    </div>
                </div>
                <nav className="flex items-center relative">
                    <Link to="/" className="inline-block p-2 font-bold hover:text-slate-400 transition-colors duration-300">
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M2.25 12l8.954-8.955c.44-.439 1.152-.439 1.591 0L21.75 12M4.5 9.75v10.125c0 .621.504 1.125 1.125 1.125H9.75v-4.875c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125V21h4.125c.621 0 1.125-.504 1.125-1.125V9.75M8.25 21h8.25" />
                      </svg>
                    </Link>
                    <button 
                        onClick={toggleLogin} 
                        className={`text-white btn shadow-[0_9px_0_rgb(0,0,0)] hover:shadow-[0_4px_0px_rgb(0,0,0)] transition-all inline-block p-2 ml-4 bg-stone-400 hover:bg-stone-500 rounded ${showLogin ? 'hover:translate-y-1' : ''}`}
                    >
                        Login
                    </button>
                    <button 
                        onClick={toggleSignup} 
                        className={`btn shadow-[0_9px_0_rgb(0,0,0)] hover:shadow-[0_4px_0px_rgb(0,0,0)] transition-all inline-block p-2 ml-4 bg-stone-500 hover:bg-stone-600 rounded ${showSignup ? 'hover:translate-y-1' : ''}`}
                    >
                        Register
                    </button>
                </nav>
            </div>
        </header>
    );
} 

export default Header;
