import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import MediaList from '../components/common/MediaList';  // Ensure the path is correct

const MediaPage = () => {
    const [mediaItems, setMediaItems] = useState([]);
    const [newMedia, setNewMedia] = useState('');
    const [medium, setMedium] = useState('Movie');
    const { username, watchlistName } = useParams();

    useEffect(() => {
        fetchMediaItems();
    }, [watchlistName]);

    const fetchMediaItems = async () => {
        const { data: watchlist } = await supabase
            .from('watchlists')
            .select('id')
            .eq('name', watchlistName)
            .single();

        if (watchlist) {
            const { data: media } = await supabase
                .from('media_items')
                .select('*')
                .eq('watchlist_id', watchlist.id)
                .order('order', { ascending: true });

            setMediaItems(media || []);
        }
    };

    const onSortEnd = ({ oldIndex, newIndex }) => {
        // This function should adjust the `order` values in your state and also trigger updates to the backend.
        // You can use the `array-move` package to easily adjust items in the array based on indices.
        const updatedItems = arrayMoveImmutable(mediaItems, oldIndex, newIndex);
        setMediaItems(updatedItems);
        updateOrderInDatabase(updatedItems);
    };

    const updateOrderInDatabase = async (items) => {
        // You can implement the database update logic here. This may include batching requests or individual updates.
    };

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-xl font-bold">{`Media in "${watchlistName}"`}</h1>
            <MediaList mediaItems={mediaItems} onSortEnd={onSortEnd} />
            <input
                type="text"
                value={newMedia}
                onChange={(e) => setNewMedia(e.target.value)}
                placeholder="Add New Media"
                className="border p-2 mt-4"
            />
            <select
                value={medium}
                onChange={(e) => setMedium(e.target.value)}
                className="border p-2 mt-4"
            >
                <option value="Movie">Movie</option>
                <option value="Book">Book</option>
                <option value="YouTube">YouTube</option>
