import React, { useState } from 'react';
import MovieCard from './MovieCard'; // Ensure this import path is correct

function HomePage() {
  // Static movie details for Kung Fu Panda 3
  const movieDetails = {
    title: 'Kung Fu Panda 3',
    description: 'Continuing his "legendary adventures of awesomeness", Po must face two hugely epic, but different threats: one supernatural and the other a little closer to home.',
    imageUrl: 'https://image.tmdb.org/t/p/original/uVdxoD9kn28qC8VQiVA6Uif1QHl.jpg',
    medium: 'Movie', 
    rating: 'PG',
    genre: 'Animation, Adventure, Comedy'
  };

  return (
    <div className="container mx-auto mt-10 px-4">
      <h1 className="text-2xl font-bold mb-6">Movie Showcase</h1>
      <MovieCard 
        title={movieDetails.title} 
        description={movieDetails.description} 
        imageUrl={movieDetails.imageUrl}
        medium={movieDetails.medium}
        rating={movieDetails.rating} 
        genre={movieDetails.genre} 
      />
    </div>
  );
}

export default HomePage;
