import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/common/header'; // Reusing the same header for consistency
import { createClient } from '@supabase/supabase-js';

// Initialize Supabase client
const supabaseUrl = 'https://klligruouvwvqrwrxqcj.supabase.co';
const supabaseAnonKey = 'your-anon-key'; // Replace with your Supabase anon key
const supabase = createClient(supabaseUrl, supabaseAnonKey);

const ProfilePage = () => {
    const [firstName, setFirstName] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchProfile();
    }, []);

    const fetchProfile = async () => {
        const user = supabase.auth.user();

        if (!user) {
            navigate('/login'); // Redirect to login if not logged in
            return;
        }

        const { data, error } = await supabase
            .from('profiles')
            .select('first-name')
            .eq('id', user.id)
            .single();

        if (error) {
            setError(error.message);
        } else if (data) {
            setFirstName(data['first-name']);
        }
        setLoading(false);
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="min-h-screen flex flex-col bg-black">
            <Header />
            <div className="flex-grow container mx-auto p-4 text-center">
                <h1 className="text-white font-extrabold text-4xl md:text-6xl lg:text-7xl tracking-tight mt-3">Profile</h1>
                {error && <p className="text-red-500">{error}</p>}
                <div className="mt-10">
                    <p className="text-white font-bold text-xl md:text-3xl">Welcome, {firstName}</p>
                </div>
            </div>
            <footer className="bg-gray-200 text-center p-4">
                Â© 2024 Bruh (bRUH), Inc.
            </footer>
        </div>
    );
};

export default ProfilePage;
