import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import client from '../utils/supabaseClient'; // Adjust this path as necessary

const ProfilePage = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('Guest');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchProfile() {
        console.log(client.auth);
      const user = client.auth.user(); // Assuming this is synchronous; check Clerk's docs

      if (!user) {
        console.error("No user found. Redirecting to login.");
        navigate('/login');
        return;
      }

      try {
        const { data, error } = await client
          .from('profiles')
          .select('username')
          .eq('user_id', user.id)
          .single();

        if (error) throw new Error(error.message);

        setUsername(data?.username);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching profile:', error);
        navigate('/');
      }
    }

    fetchProfile();
  }, [navigate]);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="w-screen h-screen flex flex-col items-center pt-10">
      <h1 className="text-2xl mb-16 text-center">Profile: {username}</h1>
      {/* Additional JSX for the profile can be added here */}
    </div>
  );
};

export default ProfilePage;
