function HomePage({ showLogin, showSignup, toggleLogin, toggleSignup }) {
  const loginFormRef = useRef(null);
  const signupFormRef = useRef(null);
  const { user } = useUser();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (loginFormRef.current && !loginFormRef.current.contains(event.target) && showLogin) {
        toggleLogin();
      }
      if (signupFormRef.current && !signupFormRef.current.contains(event.target) && showSignup) {
        toggleSignup();
      }
    };

    // Attach the listener to the document
    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      // Clean up the listener
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showLogin, showSignup, toggleLogin, toggleSignup]);

  return (
    <div className={`bg-gray-900 text-white min-h-screen relative flex items-center justify-center`}>
      <div className="container mx-auto mt-10 px-4">
        <div className={`relative ${showLogin || showSignup ? 'blur-bg' : ''}`}>
          <SignedIn>
            {/* User signed in content */}
          </SignedIn>
          <SignedOut>
            {showLogin && (
              <div ref={loginFormRef} className="form-container">
                <div className="form-inner">
                  <SignIn />
                </div>
              </div>
            )}
            {showSignup && (
              <div ref={signupFormRef} className="form-container">
                <div className="form-inner">
                  <SignUp path="/register" signInUrl="/login" />
                </div>
              </div>
            )}
          </SignedOut>
        </div>
      </div>
    </div>
  );
}
