import React from 'react';
import { Link } from 'react-router-dom';

function SecretsManagementSection() {
  return (
    <div className="min-h-screen flex flex-col bg-black text-white">
      {/* Top Section with Title and Subtitle */}
      <div className="flex-grow flex flex-col justify-center items-start px-20 py-12">
        <span className="text-yellow-500 uppercase tracking-wide font-semibold">Cloud unlocked, growth simplified</span>
        <h2 className="text-5xl font-bold mt-4 mb-2">
          Skip the sprawl with secrets management
        </h2>
        <p className="max-w-2xl text-lg leading-relaxed mb-8">
          Manage access to secrets and protect sensitive data with identity-based security.
        </p>
        <Link to="/explore-vault" className="text-lg bg-yellow-500 hover:bg-yellow-600 font-semibold px-6 py-3 rounded transition duration-300">
          Explore Vault
        </Link>
      </div>

      {/* Illustration or Image Placeholder */}
      <div className="w-full flex-1 bg-gradient-to-r from-yellow-400 to-yellow-600">
        {/* Image or illustration would go here */}
      </div>
    </div>
  );
}

export default SecretsManagementSection;
