import React, { useState } from 'react';
import ytSearch from '@citoyasha/yt-search';

const HomePage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [videos, setVideos] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm) return;
    try {
      const result = await ytSearch(searchTerm);
      setVideos(result.videos); // Assuming the module returns an object with a videos array
    } catch (error) {
      console.error('There was an error fetching the videos:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Type to search YouTube videos"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      <div className="video-list">
        {videos.map((video, index) => (
          <div key={video.id || index} className="video-card">
            <h3>{video.title}</h3>
            <a href={video.link} target="_blank" rel="noopener noreferrer">
              <img src={video.thumbnail} alt={video.title} />
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
