// HomePage.js
import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';  // Make sure the path matches where you set up the client
import MovieCard from './MovieCard';

function HomePage() {
  const [movies, setMovies] = useState([]);
  const [newTitle, setNewTitle] = useState('');

  // Fetch movies when component mounts
  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    const { data, error } = await supabase.from('media').select();
    if (error) console.log('Error fetching movies:', error);
    else setMovies(data);
  };

  const addMovie = async () => {
    const { data, error } = await supabase
      .from('media')
      .insert([{ title: newTitle, imageUrl: 'default-image-url' }]);
    if (error) console.log('Error adding movie:', error);
    else setMovies([...movies, data[0]]);
    setNewTitle('');
  };

  const removeMovie = async (id) => {
    const { error } = await supabase
      .from('media')
      .delete()
      .match({ id });
    if (error) console.log('Error removing movie:', error);
    else setMovies(media.filter(movie => movie.id !== id));
  };

  return (
    <div className="container mx-auto mt-10 px-4">
      <h1 className="text-2xl mb-6">Media Consumption</h1>
      <input
        type="text"
        value={newTitle}
        onChange={(e) => setNewTitle(e.target.value)}
        placeholder="Enter movie title"
        className="border border-gray-300 p-2 rounded"
      />
      <button onClick={addMovie} className="ml-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
        Add Movie
      </button>
      <div>
        {media.map(movie => (
          <MovieCard key={movie.id} {...movie} onRemove={removeMovie} />
        ))}
      </div>
    </div>
  );
}

export default HomePage;
