import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useClerk } from '@clerk/clerk-react';

const ProfilePage = () => {
  const navigate = useNavigate();
  const { users } = useClerk();  // Using Clerk's hook to access user operations
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchUserProfile() {
      const userId = 'user_2V7JJKmoA9HqzHhfMqK5cpgLl56'; // Your user's ID

      try {
        const response = await users.getUser(userId);
        setUserDetails(response);
        setLoading(false);
      } catch (error) {
        console.error("Failed to fetch user details:", error);
        navigate('/login');  // Redirect or handle errors as necessary
      }
    }

    fetchUserProfile();
  }, [users, navigate]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="w-screen h-screen flex flex-col items-center pt-10">
      <h1 className="text-2xl mb-16 text-center">Profile: {userDetails?.firstName} {userDetails?.lastName}</h1>
      <div>
        <strong>Email:</strong> {userDetails?.emailAddresses[0]?.emailAddress}
        <br/>
        <strong>Account Created:</strong> {new Date(userDetails?.createdAt).toLocaleString()}
      </div>
    </div>
  );
};

export default ProfilePage;
