import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';


const SignupPage = () => {

    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        // First, sign up the user with Supabase Auth
        const { user, error: authError } = await supabase.auth.signUp({
            email,
            password,
        });

        if (authError) {
            // Check specific error codes and set a custom message
            if (authError.message.includes('23505')) { // Supabase/PostgreSQL unique violation error code
                if (authError.message.includes('email')) {
                    setError("An account with this email already exists. Forgot password?");
                } else if (authError.message.includes('username')) {
                    setError("This username is already taken.");
                } else {
                    setError(authError.message); // Generic error message
                }
            } else {
                setError(authError.message);
            }
        } else {
            // Handle successful signup
            navigate('/'); // Redirect to the profile page or dashboard
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="w-full max-w-md p-8 space-y-6 bg-white rounded-lg shadow-lg">
                <h1 className="text-2xl font-bold text-center">Create an Account</h1>
                {error && <p className="text-red-500 text-center">{error}</p>}
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Email"
                            className="w-full p-2 border border-gray-300 rounded"
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            placeholder="Username"
                            className="w-full p-2 border border-gray-300 rounded"
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Password"
                            className="w-full p-2 border border-gray-300 rounded"
                            required
                        />
                    </div>
                    <button type="submit" className="w-full bg-stone-500 hover:bg-neutral-400 text-white font-bold py-2 px-4 rounded transition-colors duration-300">
                        Sign Up
                    </button>
                </form>
                <p className="text-center text-gray-300 mt-4">
                    Already have an account? <Link to="/login" className="text-blue-500 hover:underline">Login</Link>
                </p>
            </div>
        </div>
    );
};

export default SignupPage;
