import React, { useState, useEffect } from 'react';

const Rating = ({ rating, onRatingChange }) => {
  const [hoverRating, setHoverRating] = useState(0);
  const [currentRating, setCurrentRating] = useState(rating);

  useEffect(() => {
    setCurrentRating(rating);
  }, [rating]);

  const handleMouseEnter = (value) => {
    setHoverRating(value);
  };

  const handleMouseLeave = () => {
    setHoverRating(0);
  };

  const handleMouseMove = (event, value) => {
    const rect = event.target.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const halfValue = x < rect.width / 2 ? value - 0.5 : value;
    setHoverRating(halfValue);
  };

  const handleRatingClick = (newRating) => {
    setCurrentRating(newRating);
    onRatingChange(newRating);
  };

  const handleClearRating = () => {
    setCurrentRating(0);
    onRatingChange(0);
  };

  const getFillColor = (index) => {
    if (hoverRating >= index) {
      return 'front';
    } else if (!hoverRating && currentRating >= index) {
      return 'front';
    } else if (hoverRating >= index - 0.5 && hoverRating < index) {
      return 'front half';
    } else if (currentRating >= index - 0.5 && currentRating < index) {
      return 'front half';
    } else {
      return 'back';
    }
  };

  return (
    <div className="rating-container">
      <button className="clear-button" onClick={handleClearRating}>x</button>
      {[1, 2, 3, 4, 5].map((value) => (
        <div
          key={value}
          onMouseMove={(e) => handleMouseMove(e, value)}
          onMouseEnter={() => handleMouseEnter(value)}
          onMouseLeave={handleMouseLeave}
          onClick={() => handleRatingClick(hoverRating)}
          className="star"
        >
          <svg viewBox="0 0 24 24">
            <circle cx="12" cy="12" r="10" className="back" />
            <circle cx="12" cy="12" r="10" className={getFillColor(value)} />
          </svg>
        </div>
      ))}
      <span className="ml-2 text-gray-400">{currentRating.toFixed(1)}</span>
    </div>
  );
};

export default Rating;
