import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import client from '../utils/supabaseClient'; // Adjust this path as necessary

const ProfilePage = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('Guest');
  const [loading, setLoading] = useState(true);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      if (!client) {
        console.error("Supabase client is not initialized.");
        return;
      }

      const user = await client.auth.user();
      if (!user) {
        navigate('/login');
        return;
      }

      setUserId(user.id);

      const { data, error } = await client
        .from('profiles')
        .select('username')
        .eq('user_id', user.id)
        .single();

      if (error) {
        console.error('Error fetching profile:', error.message);
        navigate('/');
        return;
      }

      setUsername(data?.username);
      setLoading(false);
    };

    fetchProfile();
  }, [navigate]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="w-screen h-screen flex flex-col items-center pt-10">
      <h1 className="text-2xl mb-16 text-center">ClerkProfile: {username}</h1>
      {/* More JSX for user profile, friends list, etc., can be added here */}
    </div>
  );
};

export default ProfilePage;
